import React, { useState, useEffect } from 'react';
import {
    CommentText,
    CommentMetadata,
    CommentGroup,
    CommentContent,
    CommentAvatar,
    CommentActions,
    CommentAuthor,
    Comment,
    Form,
    FormTextArea,
    Button,
    Icon,
    Divider,
} from 'semantic-ui-react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import Cookies from 'js-cookie';
import useDownloader from 'react-use-downloader';
import { io } from 'socket.io-client';

const TeamFeed = () => {
    const { teamId } = useParams();
    const navigate = useNavigate();
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');
    const [newFile, setNewFile] = useState(null);
    const [filePreview, setFilePreview] = useState(null);
    const [replyText, setReplyText] = useState('');
    const [replyingIndex, setReplyingIndex] = useState(null);
    const { download } = useDownloader();
    const socket = io('http://localhost:8000');

    useEffect(() => {
        const fetchMessages = async () => {
            try {
                const response = await axios.get(`http://localhost:8000/feed/${teamId}`);
                setComments(response.data);
            } catch (error) {
                console.error('Error fetching messages:', error);
            }
        };

        fetchMessages();

        // Setup Socket.io listeners
        socket.on('newMessage', (message) => {
            setComments((prevComments) => [...prevComments, message]);
        });

        socket.on('newReply', (reply) => {
            setComments((prevComments) => 
                prevComments.map(comment => 
                    comment._id === reply.commentId 
                    ? { ...comment, replies: [...comment.replies, reply] } 
                    : comment
                )
            );
        });

        // Cleanup function
        return () => {
            socket.off('newMessage');
            socket.off('newReply');
        };
    }, [teamId]);

    const getCurrentUser = async () => {
        try {
            const userId = Cookies.get('userId');
            if (!userId) {
                console.error('User ID not found in cookies.');
                return null;
            }
            const response = await axios.get(`http://localhost:8000/userAuth/${userId}`);
            return response.data;
        } catch (error) {
            console.error('Error fetching user:', error);
            return null;
        }
    };

    const handleAddComment = async () => {
        if (newComment.trim() !== '' || newFile) {
            const currentDate = new Date().toLocaleString('en-US', { hour: 'numeric', minute: 'numeric', hour12: true });
            const userId = Cookies.get('userId');

            const currentUser = await getCurrentUser();
            if (!currentUser) {
                console.error('User not found.');
                return;
            }

            const formData = new FormData();
            formData.append('textContent', newComment);
            formData.append('teamId', teamId);
            formData.append('userId', userId);
            if (newFile) {
                formData.append('uploadContent', newFile);
            }

            try {
                const response = await axios.post('http://localhost:8000/feed/', formData, {
                    headers: {
                        'Content-Type': 'multipart/form-data',
                    },
                });

                const savedMessage = response.data;
                const commentData = {
                    _id: savedMessage._id,
                    textContent: newComment,
                    uploadContent: savedMessage.uploadContent,
                    createdBy: {
                        _id: userId,
                        firstName: currentUser.firstName,
                        lastName: currentUser.lastName,
                    },
                    createdAt: currentDate,
                    replies: [],
                };

                socket.emit('newMessage', commentData);

                setNewComment('');
                setNewFile(null);
                setFilePreview(null);
            } catch (error) {
                console.error('Error adding message:', error);
            }
        }
    };

    const handleAddReply = async (commentId) => {
        if (replyText.trim() !== '') {
            const currentDate = new Date().toLocaleString('en-US', { hour: 'numeric', minute: 'numeric', hour12: true });
            const userId = Cookies.get('userId');
            const currentUser = await getCurrentUser();
            const replyData = {
                textContent: replyText,
                createdBy: {
                    _id: userId,
                    firstName: currentUser.firstName,
                    lastName: currentUser.lastName,
                },
                createdAt: currentDate,
                commentId: commentId
            };

            try {
                const response = await axios.post(`http://localhost:8000/feed/${commentId}/reply`, {
                    textContent: replyText,
                    userId: userId,
                });

                socket.emit('newReply', { ...replyData, _id: response.data._id });

                setReplyText('');
                setReplyingIndex(null);
            } catch (error) {
                console.error('Error adding reply:', error);
            }
        }
    };

    const startReply = (index) => {
        setReplyingIndex(index);
    };

    const handleUserClick = (user) => {
        navigate('/profile', { state: { user } });
    };

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        setNewFile(file);
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setFilePreview(reader.result);
            };
            reader.readAsDataURL(file);
        } else {
            setFilePreview(null);
        }
    };

    const handleFileDownload = (fileUrl) => {
        const filename = fileUrl.split('/').pop();
        download(fileUrl, filename);
    };

    return (
        <div style={{ paddingLeft: '100px', marginTop: '7rem', position: 'relative', overflow: 'hidden' }}>
            <Divider horizontal>Feed</Divider>
            <div style={{ paddingBottom: '4rem', marginBottom: '4rem' }}>
                <CommentGroup threaded size='large'>
                    {comments.map((comment, index) => (
                        <Comment key={comment._id}>
                            <CommentAvatar as='a' src={'https://react.semantic-ui.com/images/avatar/small/matt.jpg'} />
                            <CommentContent>
                                <CommentAuthor as='a' onClick={() => handleUserClick(comment.createdBy)}>
                                    {comment.createdBy.firstName} {comment.createdBy.lastName}
                                </CommentAuthor>
                                <CommentMetadata>
                                    <span>{comment.createdAt}</span>
                                </CommentMetadata>
                                <CommentText>{comment.textContent}</CommentText>
                                {comment.uploadContent && (
                                    <CommentText>
                                        <Button
                                            icon
                                            labelPosition='left'
                                            onClick={() =>
                                                handleFileDownload(`http://localhost:8000/feed${comment.uploadContent}`)
                                            }
                                        >
                                            <Icon name='file alternate' />
                                            {`http://localhost:8000/feed${comment.uploadContent}`.split('/').pop()}
                                        </Button>
                                    </CommentText>
                                )}
                                <CommentActions>
                                    <a onClick={() => startReply(index)}>Reply</a>
                                </CommentActions>
                                {comment.replies && comment.replies.length > 0 && (
                                    <CommentGroup style={{ marginTop: '0.1px' }}>
                                        {comment.replies.map((reply) => (
                                            <Comment key={reply._id}>
                                                <CommentAvatar
                                                    as='a'
                                                    src={'https://react.semantic-ui.com/images/avatar/small/matt.jpg'}
                                                />
                                                <CommentContent>
                                                    <CommentAuthor as='a' onClick={() => handleUserClick(reply.createdBy)}>
                                                        {reply.createdBy.firstName} {reply.createdBy.lastName}
                                                    </CommentAuthor>
                                                    <CommentMetadata>
                                                        <span>{reply.createdAt}</span>
                                                    </CommentMetadata>
                                                    <CommentText>{reply.textContent}</CommentText>
                                                </CommentContent>
                                            </Comment>
                                        ))}
                                    </CommentGroup>
                                )}
                                {replyingIndex === index && (
                                    <Form reply>
                                        <FormTextArea
                                            value={replyText}
                                            onChange={(e) => setReplyText(e.target.value)}
                                            placeholder='Write a reply...'
                                        />
                                        <Button
                                            content='Add Reply'
                                            labelPosition='left'
                                            icon='edit'
                                            primary
                                            onClick={() => handleAddReply(comment._id)}
                                        />
                                    </Form>
                                )}
                            </CommentContent>
                        </Comment>
                    ))}
                </CommentGroup>
            </div>
            <div style={{ position: 'relative', bottom: '0', width: '50%', background: 'white', padding: '1rem' }}>
                <Form reply>
                    <FormTextArea
                        value={newComment}
                        onChange={(e) => setNewComment(e.target.value)}
                        placeholder='Send a message'
                    />
                    {filePreview && (
                        <div>
                            <img src={filePreview} alt="Preview" style={{ maxHeight: '200px', maxWidth: '200px' }} />
                            <Button icon='close' onClick={() => setFilePreview(null)} />
                        </div>
                    )}
                    <Button
                        content='Add Comment'
                        labelPosition='left'
                        icon='edit'
                        primary
                        onClick={handleAddComment}
                    />
                    <input type='file' onChange={handleFileChange} />
                </Form>
            </div>
        </div>
    );
};

export default TeamFeed;
