import React, { useContext } from 'react';
import { MessageContext } from '../api/ChatContext';

const AllChats = () => {
  const { chats } = useContext(MessageContext);

  return (
    <div className="flex justify-center items-center h-screen bg-gray-100">
      <div className="max-w-lg w-full bg-white rounded-lg shadow-lg p-6">
        <h2 className="text-xl font-semibold mb-4">All Chats</h2>
        {chats.length > 0 ? (
          <div>
            {chats.map((chat, index) => (
              <div key={index} className="mb-4">
                <p>Chat with: {chat.username}</p>
                {/* Render additional chat details or messages */}
              </div>
            ))}
          </div>
        ) : (
          <p className="text-gray-600">No chats available.</p>
        )}
      </div>
    </div>
  );
};

export default AllChats;
