import React from 'react';
import { Comment, Header, Icon } from 'semantic-ui-react';

const AllChats = () => {
  const dummyChats = [
    { id: 1, user: 'John Doe', lastMessage: 'Hey there!', timestamp: 'Just now' },
    { id: 2, user: 'Alice Smith', lastMessage: 'How are you?', timestamp: '2 hours ago' },
    { id: 3, user: 'Bob Johnson', lastMessage: 'What are you up to?', timestamp: 'Yesterday' },
    { id: 4, user: 'Emily Brown', lastMessage: 'See you later!', timestamp: '3 days ago' },
  ];

  return (
    <div style={{ padding: '2rem', backgroundColor: '#f5f5f5', height: '100vh' }}>
      <Header as='h2' icon textAlign='center'>
        <Icon name='chat' />
        Chats
      </Header>
      <Comment.Group size='large'>
        {dummyChats.map(chat => (
          <Comment key={chat.id}>
            <Comment.Content>
              <Comment.Author as='a'>{chat.user}</Comment.Author>
              <Comment.Metadata>
                <div>{chat.timestamp}</div>
              </Comment.Metadata>
              <Comment.Text>{chat.lastMessage}</Comment.Text>
            </Comment.Content>
          </Comment>
        ))}
      </Comment.Group>
    </div>
  );
};

export default AllChats;
