import React, { useState, useEffect } from 'react';
import {
    CommentText,
    CommentMetadata,
    CommentGroup,
    CommentContent,
    CommentAvatar,
    CommentActions,
    CommentAuthor,
    Comment,
    Form,
    FormTextArea,
    Button,
    Icon,
} from 'semantic-ui-react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const TeamFeed = () => {
    const { teamId } = useParams();
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');
    const [replyText, setReplyText] = useState('');
    const [replyingIndex, setReplyingIndex] = useState(null);

    useEffect(() => {
        const fetchMessages = async () => {
            try {
                const response = await axios.get(`http://localhost:8000/feed/${teamId}`);
                setComments(response.data);
            } catch (error) {
                console.error('Error fetching messages:', error);
            }
        };

        fetchMessages();
    }, [teamId]);

    const handleAddComment = async () => {
        if (newComment.trim() !== '') {
            const currentDate = new Date().toLocaleString('en-US', { hour: 'numeric', minute: 'numeric', hour12: true });
            const userId = localStorage.getItem('userId');
            const commentData = {
                textContent: newComment,
                createdBy: userId,
                createdAt: currentDate,
                replies: [],
            };

            try {
                const response = await axios.post('http://localhost:8000/feed/', {
                    textContent: newComment,
                    teamId: teamId,
                    userId: userId,
                });

                const savedMessage = response.data;
                commentData._id = savedMessage._id; // Assuming saved message returns its ID
                setComments([...comments, commentData]);
                setNewComment('');
            } catch (error) {
                console.error('Error adding message:', error);
            }
        }
    };

    return (
        <div style={{ paddingLeft: '100px', marginTop: '7rem', position: 'relative', overflow: "hidden" }}>
            <div style={{ paddingBottom: '4rem', marginBottom: '4rem' }}>
                <CommentGroup threaded size='large'>
                    {comments.map((comment, index) => (
                        <Comment key={comment._id}>
                            <CommentAvatar as='a' src='https://react.semantic-ui.com/images/avatar/small/matt.jpg' />
                            <CommentContent>
                                <CommentAuthor as='a'>{comment.createdBy.firstName} {comment.createdBy.lastName}</CommentAuthor>
                                <CommentMetadata>
                                    <span>{comment.createdAt}</span>
                                </CommentMetadata>
                                <CommentText>{comment.textContent}</CommentText>
                                <CommentActions>
                                    <a onClick={() => startReply(index)}>Reply</a>
                                </CommentActions>
                            </CommentContent>
                        </Comment>
                    ))}
                </CommentGroup>

            </div>
            <div style={{ position: 'fixed', bottom: '0', width: '50%', background: 'white', padding: '1rem' }}>
                <Form reply>
                    <FormTextArea
                        value={newComment}
                        onChange={(e) => setNewComment(e.target.value)}
                        placeholder='Send a message'
                    />
                    <Button
                        content='Send'
                        labelPosition='left'
                        icon='send'
                        onClick={handleAddComment}
                    />
                    <Button icon labelPosition='left' >
                        <Icon name='upload' />
                        Upload File
                    </Button>
                </Form>
            </div>
        </div>
    );
};

export default TeamFeed;
