import React, { useEffect, useState, useContext } from 'react';
import { Card, Image, Icon, Segment } from 'semantic-ui-react';
import axios from 'axios';
import { authContext } from '../api/AuthContext';
import Cookies from 'js-cookie';

const UserDetails = () => {
    const { user } = useContext(authContext); // Get user from authContext
    const [userDetails, setUserDetails] = useState(null);

    useEffect(() => {
        const userId = Cookies.get('userId');
        if (userId) {
            fetchUserDetails(userId);
        }
    }, []);

    const fetchUserDetails = async (userId) => {
        try {
            const response = await axios.get(`http://localhost:8000/userAuth/${userId}`);
            setUserDetails(response.data);
        } catch (error) {
            console.error('Error fetching user details:', error);
        }
    };

    if (!userDetails) {
        return <Segment loading style={{ minHeight: '100vh' }} />;
    }

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '100vh', background: '#f7f7f7' }}>
            <Card>
                <Image src={userDetails.avatar || 'https://react.semantic-ui.com/images/avatar/large/elliot.jpg'} wrapped ui={false} />
                <Card.Content>
                    <Card.Header>{userDetails.firstName} {userDetails.lastName}</Card.Header>
                    <Card.Meta>
                        <span className='date'>Joined in {new Date(userDetails.createdAt).getFullYear()}</span>
                    </Card.Meta>
                    <Card.Description>
                        Email: {userDetails.email}
                    </Card.Description>
                    <Card.Description>
                        Role: {userDetails.role}
                    </Card.Description>
                </Card.Content>
                <Card.Content extra>
                    <a>
                        <Icon name='user' />
                        {userDetails.friendsCount} Friends
                    </a>
                </Card.Content>
            </Card>
        </div>
    );
};

export default UserDetails;
