import React from 'react';
import { useLocation } from 'react-router-dom';
import { Card, Icon, Image, Grid } from 'semantic-ui-react';

const UserProfile = () => {
    const location = useLocation();
    const { user } = location.state || {};

    if (!user) {
        return <div>No user data available</div>;
    }

    return (
        <div className="flex items-center justify-center bg-gray-100">
            <Card centered className="p-6 w-full max-w-2xl bg-white shadow-lg border border-gray-200">
                <Grid columns={2} stackable>
                    <Grid.Column width={6} className="flex justify-center">
                        <Image
                            src="https://react.semantic-ui.com/images/avatar/large/matthew.png"
                            size="large"
                            circular
                        />
                    </Grid.Column>
                    <Grid.Column width={10}>
                        <Card.Content>
                            <Card.Header className="text-4xl font-bold text-gray-800">{user.firstName} {user.lastName}</Card.Header>
                            <Card.Meta className="text-lg text-gray-500 mb-4">{user.email}</Card.Meta>
                            <Card.Description className="text-lg text-gray-700">
                                <p><Icon name='mail' /> Email: {user.email}</p>
                                <p><Icon name='user' /> Gender: {user.gender}</p>
                            </Card.Description>
                        </Card.Content>
                        <Card.Content extra>
                            <div className="flex justify-around mt-4">
                                <a href="#" className="text-blue-600 hover:text-blue-800">
                                    <Icon name='facebook' size='large' />
                                </a>
                                <a href="#" className="text-blue-500 hover:text-blue-700">
                                    <Icon name='twitter' size='large' />
                                </a>
                                <a href="#" className="text-pink-600 hover:text-pink-800">
                                    <Icon name='instagram' size='large' />
                                </a>
                                <a href="#" className="text-gray-600 hover:text-gray-800">
                                    <Icon name='linkedin' size='large' />
                                </a>
                            </div>
                        </Card.Content>
                    </Grid.Column>
                </Grid>
            </Card>
        </div>
    );
};

export default UserProfile;
