import React, { useState } from 'react';
import { Card, Form, Button, Container, Message } from 'semantic-ui-react';


const genderOptions = [
    { key: 'm', text: 'Male', value: 'male' },
    { key: 'f', text: 'Female', value: 'female' },
    { key: 'o', text: 'Other', value: 'other' },
]


const AuthPage = () => {
    const navigate = useNavigate();
    const isLoggedIn = useContext(isUserLoggedIn);

    const [signUpForm, setSignUpForm] = useState({
        firstName: '',
        lastName: '',
        gender: '',
        email: '',
        password: ''
    });

    const [loginForm, setLoginForm] = useState({
        email: '',
        password: ''
    });

    const handleSignUpChange = (e, { name, value }) => {
        setSignUpForm({ ...signUpForm, [name]: value });
    };

    const handleLoginChange = (e, { name, value }) => {
        setLoginForm({ ...loginForm, [name]: value });
    };

    const handleSignUpSubmit = async () => {
        try {
            const response = await axios.post('http://localhost:8000/user/signup', signUpForm);
            console.log('Sign Up Response:', response.data);
            isLoggedIn.setUserState('Logged-In');
        } catch (error) {
            console.error('Error signing up:', error);
        }
    };

    const handleLoginSubmit = async () => {
        try {
            const response = await axios.post('http://localhost:8000/user/login', loginForm);
            // console.log('Login Response:', response.data);
            const loggedinToken = response.data.token;
            // console.log("Logged in token", loggedinToken);
            const userId = response.data.userId;
            localStorage.setItem('userId', userId);
            // console.log("THE USER ID IS:", userId);
            isLoggedIn.login(loggedinToken);
            console.log("After calling login");
        } catch (error) {
            console.log('Error logging in:', error);
        }
    };



    if (isLoggedIn.userState === 'Logged-In') {
        navigate('/');
    }

  return (
    <Container text>
      <Card fluid>
        <Card.Content>
          <Card.Header>{isSignup ? 'Sign Up' : 'Login'}</Card.Header>
        </Card.Content>
        <Card.Content>
          <Form onSubmit={handleSubmit}>
            {isSignup && (
              <>
                <Form.Input
                  label='First Name'
                  name='firstName'
                  value={formData.firstName}
                  onChange={handleChange}
                  required
                />
                <Form.Input
                  label='Last Name'
                  name='lastName'
                  value={formData.lastName}
                  onChange={handleChange}
                  required
                />
                <Form.Select
                  label='Gender'
                  name='gender'
                  options={[
                    { key: 'm', text: 'Male', value: 'male' },
                    { key: 'f', text: 'Female', value: 'female' },
                    { key: 'o', text: 'Other', value: 'other' },
                  ]}
                  value={formData.gender}
                  onChange={handleChange}
                  required
                />
              </>
            )}
            <Form.Input
              label='Email'
              type='email'
              name='email'
              value={formData.email}
              onChange={handleChange}
              required
            />
            <Form.Input
              label='Password'
              type='password'
              name='password'
              value={formData.password}
              onChange={handleChange}
              required
            />
            {error && <Message negative>{error}</Message>}
            <Button primary type='submit'>
              {isSignup ? 'Sign Up' : 'Login'}
            </Button>
          </Form>
        </Card.Content>
        <Card.Content extra>
          <Button onClick={toggleForm}>
            {isSignup ? 'Already have an account? Login' : 'Don\'t have an account? Sign Up'}
          </Button>
        </Card.Content>
      </Card>
    </Container>
  );
};

export default AuthPage;
