import React, { useState } from 'react';
import SearchBar from '../Components/SearchBar';
import { useNavigate } from 'react-router-dom';

const AllChats = () => {
    const [members, setMembers] = useState([]); // Array to store selected members
    const navigate = useNavigate();
    const handleUserSelect = (selectedUser) => {
        // setMembers([...members, selectedUser]);
        navigate(`/chat/${selectedUser}`)
    };

    return (
        <div className="container mx-auto px-4 py-8">
            <div className="flex justify-between items-center mb-4">
                <h1 className="text-2xl font-bold">Chat Page</h1>
                <button
                    className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded"
                    onClick={() => {
                        // Logic to open add members modal or handle adding members
                        console.log('Add members clicked');
                    }}
                >
                    Add Members <i className="fas fa-plus ml-2"></i>
                </button>
            </div>

            <div className="flex space-x-4 mb-4">
                {/* Display selected members */}
                {members.map((member) => (
                    <div key={member._id} className="bg-gray-200 p-2 rounded">
                        <span className="font-bold">{member.firstName} {member.lastName}</span>
                        <span className="text-gray-600 ml-2">{member.email}</span>
                    </div>
                ))}
            </div>
            {/* Search bar to add new members */}
            <SearchBar onUserSelect={handleUserSelect} />

            {/* Chat messages section */}
            <div className="border-t pt-4">
                {/* Render chat messages here */}
                {/* Example: You can map over messages and display them */}
                {/* <MessageList messages={messages} /> */}
                {/* Replace 'MessageList' with your actual component */}
            </div>
        </div>
    );
};

export default AllChats;
