import React from 'react';
import { useLocation } from 'react-router-dom';
import { Card, Image } from 'semantic-ui-react';

const UserProfile = () => {
    const location = useLocation();
    const { user } = location.state || {};

    if (!user) {
        return <div className="text-center text-gray-500 mt-20">No user data available</div>;
    }

    return (
        <div className="flex justify-center items-center h-screen bg-gray-100">
            <Card>
                <Image src={user.profileImageURL ||"https://react.semantic-ui.com/images/avatar/large/matthew.png"} wrapped ui={false} />
                <Card.Content>
                    <Card.Header>{user.firstName} {user.lastName}</Card.Header>
                    <Card.Meta>
                        <span className="text-gray-500">{user.email}</span>
                    </Card.Meta>
                    <Card.Description>
                        <p className="text-gray-700">Gender: {user.gender}</p>
                    </Card.Description>
                </Card.Content>
            </Card>
        </div>
    );
};

export default UserProfile;
