import React, { useState, useEffect } from 'react';
import {
    CommentText,
    CommentMetadata,
    CommentGroup,
    CommentContent,
    CommentAvatar,
    CommentActions,
    CommentAuthor,
    Comment,
    Form,
    FormTextArea,
    Button,
    Icon,
} from 'semantic-ui-react';
import axios from 'axios';

const TeamFeed = ({ teamId }) => {
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');
    const [replyText, setReplyText] = useState('');
    const [replyingIndex, setReplyingIndex] = useState(null);

    useEffect(() => {
        const fetchMessages = async () => {
            try {
                const response = await axios.get(`http://localhost:8000/feed/?teamId=${teamId}`);
                setComments(response.data);
            } catch (error) {
                console.error('Error fetching messages:', error);
            }
        };

        fetchMessages();
    }, [teamId]);

    const handleAddComment = async () => {
        if (newComment.trim() !== '') {
            const currentDate = new Date().toLocaleString('en-US', { hour: 'numeric', minute: 'numeric', hour12: true });
            const commentData = {
                author: 'You',
                timestamp: currentDate,
                text: newComment,
                replies: [],
            };

            try {
                const response = await axios.post('http://localhost:8000/feed/', {
                    textContent: newComment,
                    teamId: teamId,
                    userId: 'yourUserIdHere', // Replace with actual userId logic
                });

                const savedMessage = response.data;
                commentData._id = savedMessage._id; // Assuming saved message returns its ID
                setComments([...comments, commentData]);
                setNewComment('');
            } catch (error) {
                console.error('Error adding message:', error);
            }
        }
    };

    const handleAddReply = async (index) => {
        if (replyText.trim() !== '') {
            const currentDate = new Date().toLocaleString('en-US', { hour: 'numeric', minute: 'numeric', hour12: true });
            const newReply = {
                author: 'You', 
                timestamp: currentDate,
                text: replyText,
            };

            try {
                const response = await axios.post('http://localhost:8000/feed/', {
                    textContent: replyText,
                    teamId: teamId,
                    userId: 'yourUserIdHere', // Replace with actual userId logic
                });

                const savedMessage = response.data;
                newReply._id = savedMessage._id; // Assuming saved message returns its ID

                const updatedComments = [...comments];
                updatedComments[index].replies.push(newReply);
                setComments(updatedComments);
                setReplyText('');
                setReplyingIndex(null);
            } catch (error) {
                console.error('Error adding reply:', error);
            }
        }
    };

    const startReply = (index) => {
        setReplyingIndex(index);
    };

    return (
        <div style={{ paddingLeft: '100px', marginTop: '7rem', position: 'relative', overflow:"hidden" }}>
            <div style={{ paddingBottom: '4rem', marginBottom: '4rem' }}>
                <CommentGroup threaded size='large'>
                    {comments.map((comment, index) => (
                        <Comment key={comment._id}>
                            <CommentAvatar as='a' src='https://react.semantic-ui.com/images/avatar/small/matt.jpg' />
                            <CommentContent>
                                <CommentAuthor as='a'>{comment.author}</CommentAuthor>
                                <CommentMetadata>
                                    <span>{comment.timestamp}</span>
                                </CommentMetadata>
                                <CommentText>{comment.textContent}</CommentText>
                                <CommentActions>
                                    <a onClick={() => startReply(index)}>Reply</a>
                                </CommentActions>
                                {comment.replies.length > 0 && (
                                    <CommentGroup>
                                        {comment.replies.map((reply, replyIndex) => (
                                            <Comment key={reply._id}>
                                                <CommentAvatar as='a' src='https://react.semantic-ui.com/images/avatar/small/jenny.jpg' />
                                                <CommentContent>
                                                    <CommentAuthor as='a'>{reply.author}</CommentAuthor>
                                                    <CommentMetadata>
                                                        <span>{reply.timestamp}</span>
                                                    </CommentMetadata>
                                                    <CommentText>{reply.textContent}</CommentText>
                                                </CommentContent>
                                            </Comment>
                                        ))}
                                    </CommentGroup>
                                )}
                                {replyingIndex === index && (
                                    <Form reply>
                                        <FormTextArea
                                            value={replyText}
                                            onChange={(e) => setReplyText(e.target.value)}
                                            placeholder='Write a reply...'
                                        />
                                        <Button
                                            content='Add Reply'
                                            labelPosition='left'
                                            icon='edit'
                                            primary
                                            onClick={() => handleAddReply(index)}
                                        />
                                    </Form>
                                )}
                            </CommentContent>
                        </Comment>
                    ))}
                </CommentGroup>
            </div>
            <div style={{ position: 'fixed', bottom: '0', width: '50%', background: 'white', padding: '1rem' }}>
                <Form reply>
                    <FormTextArea
                        value={newComment}
                        onChange={(e) => setNewComment(e.target.value)}
                        placeholder='Send a message'
                    />
                    <Button
                        content='Send'
                        labelPosition='left'
                        icon='send'
                        onClick={handleAddComment}
                    />
                    <Button icon labelPosition='left' >
                        <Icon name='upload' />  
                        Upload File
                    </Button>
                </Form>
            </div>
        </div>
    );
};

export default TeamFeed;
