import React, { useState } from 'react';
import { Icon, MenuItem, Input, Menu } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';

const NavBar = () => {
    const [darkMode, setDarkMode] = useState(false);

    const toggleDarkMode = () => {
        setDarkMode(!darkMode);
        if (darkMode) {
            document.documentElement.classList.remove('dark');
        } else {
            document.documentElement.classList.add('dark');
        }
    };

    return (
        <div className={`flex flex-col h-screen ${darkMode ? 'bg-gray-900' : 'bg-white'}`}>
            {/* TOPBAR */}
            <div className={`flex items-center justify-between p-4 ${darkMode ? 'bg-gray-800' : 'bg-white'} text-white h-16 fixed w-full z-10`}>
                <div className="flex items-center justify-start w-1/3">
                    <img src="https://react.semantic-ui.com/logo.png" alt="App Logo" className="h-10 w-10" />
                </div>
                <div className="flex items-center justify-center w-1/3">
                    <Input icon="search" placeholder="Search..." className="mr-4" size="big" />
                </div>
                <div className="flex items-center justify-end w-1/3">
                    <Icon name="sun" size="big" className="mr-4 cursor-pointer" color="black" onClick={toggleDarkMode} />
                    <Icon name="settings" size="big" className="mr-4 cursor-pointer" color="black" />
                    <Icon name="user circle" size="big" className="cursor-pointer" color="black" />
                </div>
            </div>
            {/* SIDEBAR */}
            <div className="flex flex-1 pt-16">
                <Menu vertical fixed="left" className="h-full w-56" style={{ marginTop: '4rem', width: "10rem", borderTop: "1px solid", borderRight: "1px solid" }}>
                    <MenuItem>
                        <Icon name="home" />
                        Home
                    </MenuItem>
                    <MenuItem>
                        <Icon name="chart bar" />
                        Activity
                    </MenuItem>
                    <MenuItem>
                        <Icon name="chat" />
                        Chat
                    </MenuItem>
                    <MenuItem>
                        <Icon name="calendar" />
                        Calendar
                    </MenuItem>
                    <MenuItem>
                        <Icon name="settings" />
                        Settings
                    </MenuItem>
                </Menu>
            </div>
        </div>
    );
};

export default NavBar;
