import React, { useState } from 'react';
import { Button, Form, Comment, Icon, Divider } from 'semantic-ui-react';
import { redirect } from 'react-router-dom';
import SearchBar from '../Components/SearchBar';

const AllChats = () => {
  const [chats, setChats] = useState([]);
  const [selectedUserId, setSelectedUserId] = useState(null);

  const handleUserSelect = (selectedUser) => {
    if (selectedUser) {
      const existingChat = chats.find(chat => chat.member._id === selectedUser._id);
      if (existingChat) {
        setSelectedUserId(selectedUser._id);
      } else {
        // Create new chat box for selected user
        const newChat = {
          id: selectedUser._id,
          member: selectedUser,
          lastMessage: 'Start chatting...',
          timestamp: new Date().toLocaleTimeString(),
        };
        setChats([...chats, newChat]);
        setSelectedUserId(selectedUser._id);
      }
    }
  };

  if (selectedUserId) {
    return <redirect to={`/chat/${selectedUserId}`} />;
  }

  return (
    <div className="bg-gray-100 min-h-screen">
      <div className="max-w-4xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
        <h2 className="text-2xl font-bold text-center mb-6">Chats</h2>
        <div className="bg-white shadow overflow-hidden sm:rounded-lg">
          <ul>
            {chats.map(chat => (
              <li key={chat.id} className="border-t border-gray-200">
                <a href={`#${chat.id}`} className="block hover:bg-gray-50 focus:outline-none focus:bg-gray-50 transition duration-150 ease-in-out">
                  <div className="px-4 py-4 sm:px-6 w-full">
                    <div className="flex items-center justify-between">
                      <div className="text-sm leading-5 font-medium text-indigo-600 truncate">{chat.member.firstName} {chat.member.lastName}</div>
                      <div className="ml-2 flex-shrink-0 flex">
                        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium leading-4 bg-green-100 text-green-800">
                          {chat.timestamp}
                        </span>
                      </div>
                    </div>
                    <div className="mt-2 sm:flex sm:justify-between">
                      <div className="sm:flex w-full">
                        <div className="truncate max-w-full text-sm leading-5 text-gray-900">{chat.lastMessage}</div>
                      </div>
                    </div>
                  </div>
                </a>
              </li>
            ))}
          </ul>
          <div className="px-4 py-3 border-t border-gray-200 sm:px-6">
            <SearchBar onUserSelect={handleUserSelect} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default AllChats;
