import React, { useState } from 'react';
import SearchBar from '../Components/SearchBar';
import { useNavigate } from 'react-router-dom';

const AllChats = () => {
  const [chats, setChats] = useState([]); // State to store chats
  const navigate = useNavigate();

  const handleUserSelect = (selectedUser) => {
    console.log( 'YOOO',selectedUser._id)
    // Check if the user is already in chats
    if (!chats.find(chat => chat.id === selectedUser._id)) {
      // Add selected user to chats array
      setChats([...chats, {
        id: selectedUser._id,
        user: `${selectedUser.firstName} ${selectedUser.lastName}`,
        timestamp: new Date().toLocaleString(), 
        lastMessage: '', 
      }]);
      console.log("ydsds",chats);
    }
    // Navigate to chat page for selected user
    navigate(`/chat/${selectedUser._id}`);
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="px-4 py-3 border-t border-gray-200 sm:px-6">
        <SearchBar onUserSelect={handleUserSelect} />
      </div>
      <div className="bg-white shadow overflow-hidden sm:rounded-lg">
        <ul>
          {chats.map(chat => (
            <li key={chat.id} className="border-t border-gray-200">
                <div className="px-4 py-4 sm:px-6 w-full">
                  <div className="flex items-center justify-between">
                    <div className="text-sm leading-5 font-medium text-indigo-600 truncate">{chat.user}</div>
                    <div className="ml-2 flex-shrink-0 flex">
                      <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium leading-4 bg-green-100 text-green-800">
                        {chat.timestamp}
                      </span>
                    </div>
                  </div>
                  <div className="mt-2 sm:flex sm:justify-between">
                    <div className="sm:flex w-full">
                      <div className="truncate max-w-full text-sm leading-5 text-gray-900">{chat.lastMessage}</div>
                    </div>
                  </div>
                </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default AllChats;
