import React, { useState, useEffect, useContext } from 'react';
import { Icon, Menu, Dropdown, DropdownItem } from 'semantic-ui-react';
import { useNavigate } from 'react-router-dom';
import SearchBar from './SearchBar';
import { authContext } from '../api/AuthContext';
import Cookies from 'js-cookie';
import axios from 'axios';

const NavBar = () => {
    const navigate = useNavigate();
    const { logout, user } = useContext(authContext);
    const [currentUser,setCurrentUser] = useState('');
    
    useEffect(()=>{
        const getCurrentUser = async () => {
            try {
                const userId = Cookies.get('userId');
                if (!userId) {
                    console.error('User ID not found in cookies.');
                    return null;
                }
                const response = await axios.get(`http://localhost:8000/userAuth/${userId}`);
                setCurrentUser(response.data); 
            } catch (error) {
                console.error('Error fetching user:', error);
                return null;
            }
        };
        getCurrentUser();
    },[])
    
    const handleUserSelect = (user) => {
        navigate('/profile', { state: { user } });
    };

    const handleLogout = () => {
        logout(); // Call the logout function from authContext
        navigate('/auth'); // Redirect to login or another appropriate route after logout
    };

    return (
        <div className="flex flex-col">
            {/* TOPBAR */}
            <div className="flex items-center justify-between p-4 bg-white text-white h-16 fixed w-full z-10">
                <div className="flex items-center justify-start w-1/3">
                    <img src="https://react.semantic-ui.com/logo.png" alt="App Logo" className="h-10 w-10" />
                </div>
                <div className="flex items-center justify-center w-1/3">
                    <SearchBar onUserSelect={handleUserSelect} />
                </div>
                <div className="flex items-center justify-end w-1/3">
                    <Dropdown icon={<Icon name="user circle" size="big" color="black" />} pointing="top right">
                        <Dropdown.Menu>
                            <Dropdown.Header>Hi {currentUser.firstName} {currentUser.lastName} </Dropdown.Header>  
                            <Dropdown.Divider />      
                            <Dropdown.Item icon="user" text="Profile" onClick={() => navigate('/userProfile')} />   
                            <Dropdown.Item icon="sign-out" text="Logout" onClick={handleLogout} />
                        </Dropdown.Menu>
                    </Dropdown>
                </div>
            </div>
            {/* CONTENT */}
            <div className="flex mt-20">
                {/* SIDEBAR */}
                <Menu vertical fixed="left" className="h-full w-65 bg-white" style={{ marginTop: '7.6rem' }}>
                    <Menu.Item onClick={() => navigate('/')}>
                        <Icon name="sitemap" />
                        Teams
                    </Menu.Item>
                    <Menu.Item>
                        <Icon name="tasks" />
                        Tasks
                    </Menu.Item>
                    <Menu.Item>
                        <Icon name="chat" />
                        Chat
                    </Menu.Item>
                    <Menu.Item>
                        <Icon name="calendar" />
                        Calendar
                    </Menu.Item>
                    <Menu.Item>
                        <Icon name="settings" />
                        Settings
                    </Menu.Item>
                </Menu>
            </div>
        </div>
    );
};

export default NavBar;
