import React from 'react';

const AllChats = () => {
  const dummyChats = [
    { id: 1, user: 'John Doe', lastMessage: 'Hey there!', timestamp: 'Just now' },
    { id: 2, user: 'Alice Smith', lastMessage: 'How are you?', timestamp: '2 hours ago' },
    { id: 3, user: 'Bob Johnson', lastMessage: 'What are you up to?', timestamp: 'Yesterday' },
    { id: 4, user: 'Emily Brown', lastMessage: 'See you later!', timestamp: '3 days ago' },
  ];

  return (
    <div className="bg-gray-100 min-h-screen">
      <div className="max-w-2xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
        <h2 className="text-2xl font-bold text-center mb-6">Chats</h2>
        <div className="bg-white shadow overflow-hidden sm:rounded-lg">
          <ul>
            {dummyChats.map(chat => (
              <li key={chat.id} className="border-t border-gray-200">
                <a href={`#${chat.id}`} className="block hover:bg-gray-50 focus:outline-none focus:bg-gray-50 transition duration-150 ease-in-out">
                  <div className="px-4 py-4 sm:px-6">
                    <div className="flex items-center justify-between">
                      <div className="text-sm leading-5 font-medium text-indigo-600 truncate">{chat.user}</div>
                      <div className="ml-2 flex-shrink-0 flex">
                        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium leading-4 bg-green-100 text-green-800">
                          {chat.timestamp}
                        </span>
                      </div>
                    </div>
                    <div className="mt-2 sm:flex sm:justify-between">
                      <div className="sm:flex">
                        <div className="truncate max-w-xs text-sm leading-5 text-gray-900">{chat.lastMessage}</div>
                      </div>
                    </div>
                  </div>
                </a>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default AllChats;
