import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const AllChats = () => {
  const [chats, setChats] = useState([]);

  useEffect(() => {
    const fetchChats = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/chat/all/${Cookies.get('userId')}`);
        setChats(response.data);
      } catch (error) {
        console.error('Error fetching chats:', error);
      }
    };

    fetchChats();
  }, []);

  return (
    <div style={{ paddingLeft: '100px', marginTop: '7rem' }}>
      <h1>All Chats</h1>
      <div>
        {chats.map((userId) => (
          <div key={userId}>
            <Link to={`/chat/${userId}`}>
              <div style={{ border: '1px solid #ccc', padding: '10px', marginBottom: '10px', cursor: 'pointer' }}>
                Chat with User ID: {userId}
              </div>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AllChats;
