import React, { useContext } from 'react';
import { MessageContext } from '../api/ChatContext';

const AllChats = () => {
  const { selectedUser } = useContext(MessageContext);
  console.log(selectedUser)
  return (
    <div className="flex justify-center items-center h-screen bg-gray-100">
      {selectedUser ? (
        <div className="max-w-lg w-full bg-white rounded-lg shadow-lg p-6">
          <h2 className="text-xl font-semibold mb-4">Chat with {selectedUser.firstName}</h2>
          {/* Render messages or message components */}
        </div>
      ) : (
        <p className="text-gray-600">Select a user to start chatting.</p>
      )}
    </div>
  );
};

export default AllChats;
