import React from 'react';
import { useLocation } from 'react-router-dom';
import { Card, Icon, Image } from 'semantic-ui-react';

const UserProfile = () => {
    const location = useLocation();
    const { user } = location.state || {};

    if (!user) {
        return <div>No user data available</div>;
    }

    return (
        <div className="flex justify-center items-center h-screen bg-gray-100">
            <Card className="w-full max-w-md p-4 shadow-lg">
                <div className="flex justify-center">
                    <Image src='https://react.semantic-ui.com/images/avatar/large/matthew.png' size='small' circular />
                </div>
                <Card.Content>
                    <Card.Header className="text-center text-2xl mt-2">
                        {user.firstName} {user.lastName}
                    </Card.Header>
                    <Card.Meta className="text-center text-gray-600">
                        {user.email}
                    </Card.Meta>
                    <Card.Description className="mt-4">
                        <div className="flex justify-between">
                            <div className="font-semibold">Gender:</div>
                            <div>{user.gender}</div>
                        </div>
                        <div className="flex justify-between mt-2">
                            <div className="font-semibold">Phone:</div>
                            <div>{user.phone}</div>
                        </div>
                        <div className="flex justify-between mt-2">
                            <div className="font-semibold">Address:</div>
                            <div>{user.address}</div>
                        </div>
                        <div className="flex justify-between mt-2">
                            <div className="font-semibold">Date of Birth:</div>
                            <div>{user.dob}</div>
                        </div>
                    </Card.Description>
                </Card.Content>
                <Card.Content extra className="flex justify-center">
                    <Icon name='user' /> Member since {user.joinDate}
                </Card.Content>
            </Card>
        </div>
    );
};

export default UserProfile;
