import React, { useContext } from 'react';
import { MessageContext } from '../api/ChatContext'; // Ensure this path is correct
import { useNavigate } from 'react-router-dom';

const AllChats = () => {
  const navigate = useNavigate();
  const { chats } = useContext(MessageContext);

  const handleChatClick = (user) => {
    navigate(`/chat/${user.userId}`);
  };

  return (
    
<div className="container mx-auto px-4 py-8"><div className="px-4 py-3 border-t border-gray-200 sm:px-6">
      {/* <SearchBar onUserSelect={handleUserSelect} /> */}
    </div>
      <div className="bg-white shadow overflow-hidden sm:rounded-lg">
        <ul>
        {chats.length > 0 ? (
          <div>
            {chats.map((chat, index) => (
              <div key={index} className="mb-4 cursor-pointer hover:bg-gray-200 p-4 rounded-lg" onClick={() => handleChatClick(chat)}>
                <p className="text-lg font-medium">{chat.firstName} {chat.lastName}</p>
              </div>
            ))}
          </div>
        ) : (
          <p className="text-gray-600">No chats available.</p>
        )}
        </ul>

      </div>
    </div>
  );
};

export default AllChats;
