import React, { useContext, useEffect, useState } from 'react';
import { Button, Header, Image, List, Accordion, Icon, Menu } from 'semantic-ui-react';
import { teamContext } from '../api/TeamsContext';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const TeamDetails = () => {
  const { teams, setTeams } = useContext(teamContext);
  const [userTeam, setTeam] = useState(null);
  const { teamId } = useParams();
  const navigate = useNavigate();
  const [activeIndex, setActiveIndex] = useState(-1);
  const [user, setUser] = useState(null); // Assuming you have a way to get the logged-in user

  useEffect(() => {
    const fetchTeam = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/team/userTeam/${teamId}`);
        setTeam(response.data);
      } catch (error) {
        console.error('Error fetching team:', error);
      }
    };

    fetchTeam();
  }, [teamId]);

  useEffect(() => {
    // Fetch the logged-in user details and set it to `user` state
    // Example:
    // axios.get('/api/currentUser').then(response => setUser(response.data));
  }, []);

  const handleClick = (e, titleProps) => {
    const { index } = titleProps;
    const newIndex = activeIndex === index ? -1 : index;
    setActiveIndex(newIndex);

    if (index === 1 && newIndex === 1) {
      navigate(`/teamDetails/${teamId}/files`, { state: { teamId } });
    }

    if (index === 2 && newIndex === 2) {
      navigate(`/teamDetails/${teamId}/tasks`, { state: { teamId } });
    }
  };

  const handleUserClick = (user) => {
    navigate('/profile', { state: { user } });
  };

  const handleDeleteTeam = async () => {
    try {
      await axios.delete(`http://localhost:8000/team/${teamId}`, { data: { userId: user._id } });
      navigate('/teams'); // Redirect to teams list after deletion
    } catch (error) {
      console.error('Error deleting team:', error);
    }
  };

  const handleUpdateTeam = async (newTeamName, addMembers, removeMembers) => {
    try {
      const updateData = {
        userId: user._id,
        teamName: newTeamName,
        addMembers: addMembers,
        removeMembers: removeMembers,
      };
      const response = await axios.put(`http://localhost:8000/team/${teamId}`, updateData);
      setTeam(response.data);
    } catch (error) {
      console.error('Error updating team:', error);
    }
  };

  return (
    <div className="sticky top-16 bg-white shadow-md z-10" style={{ marginLeft: "100px" }}>
      <Header as='h1' attached dividing textAlign='center' style={{ backgroundColor: "lightgrey" }}>
        {userTeam && userTeam.teamName}
      </Header>
      <Menu vertical fluid>
        <Accordion styled fluid>
          <Accordion.Title
            active={activeIndex === 0}
            index={0}
            onClick={handleClick}
          >
            <Icon name='dropdown' />
            Team Members
          </Accordion.Title>
          <Accordion.Content active={activeIndex === 0}>
            <List divided verticalAlign='middle'>
              {userTeam && userTeam.members && userTeam.members.map(member => (
                <List.Item key={member._id}>
                  <List.Content floated='right'>
                    <Button>Message</Button>
                  </List.Content>
                  <Image avatar src='https://react.semantic-ui.com/images/avatar/small/lena.png' />
                  <List.Content as='a' onClick={() => handleUserClick(member)}>
                    {member.firstName} {member.lastName}
                    {userTeam.admin === member._id && <span style={{ color: 'red', marginLeft: '10px' }}>Admin</span>}
                  </List.Content>
                </List.Item>
              ))}
            </List>
          </Accordion.Content>

          <Accordion.Title
            active={activeIndex === 1}
            index={1}
            onClick={handleClick}
          >
            <Icon name='dropdown' />
            Files
          </Accordion.Title>

          <Accordion.Title
            active={activeIndex === 2}
            index={2}
            onClick={handleClick}
          >
            <Icon name='dropdown' />
            Tasks
          </Accordion.Title>
        </Accordion>
      </Menu>
      {userTeam && user && userTeam.admin === user._id && (
        <div style={{ marginTop: '20px' }}>
          <Button color='red' onClick={handleDeleteTeam}>Delete Team</Button>
          <Button color='blue' onClick={() => handleUpdateTeam('New Team Name', ['newMemberId1'], ['removeMemberId1'])}>Update Team</Button>
        </div>
      )}
    </div>
  );
};

export default TeamDetails;
