import axios from 'axios';
import React, { useEffect, useState } from 'react';

const GetImage = () => {
  const [image, setImage] = useState(null);
  const [allImages, setAllImages] = useState([]);

  useEffect(() => {
    getImages();
  }, []);

  const submitImage = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('profileImage', image);
    // formData.append('firstName', 'John'); // Replace with actual data
    // formData.append('lastName', 'Doe');   // Replace with actual data
    // formData.append('email', 'john.doe@example.com'); // Replace with actual data
    // formData.append('gender', 'male');    // Replace with actual data
    // formData.append('password', 'password123'); // Replace with actual data

    try {
      await axios.post('http://localhost:8000/userAuth/signup', formData);
      getImages(); // Refresh images after upload
    } catch (error) {
      console.error('Error uploading image:', error);
    }
  };

  const onInputChange = (e) => {
    setImage(e.target.files[0]);
  };

  const getImages = async () => {
    try {
      const result = await axios.get('http://localhost:8000/userAuth/get-images');
      setAllImages(result.data.data);
    } catch (error) {
      console.error('Error fetching images:', error);
    }
  };

  return (
    <div>
      <form onSubmit={submitImage}>
        <input type="file" accept="image/*" onChange={onInputChange}></input>
        <button type="submit">Submit</button>
      </form>
      <div>
        {allImages.map((user, index) => (
          <img key={index} src={`http://localhost:8000/userAuth${user.profileImageURL}`} alt="Profile" height={100} width={100} />
        ))}
      </div>
    </div>
  );
};

export default GetImage;
