import React, { useState } from 'react';
import {
    CommentText,
    CommentMetadata,
    CommentGroup,
    CommentContent,
    CommentAvatar,
    CommentActions,
    CommentAuthor,
    Comment,
    Form,
    FormTextArea,
    Button,
    Icon,
} from 'semantic-ui-react';

const TeamFeed = () => {
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');
    const [replyText, setReplyText] = useState('');
    const [replyingIndex, setReplyingIndex] = useState(null);

    // Function to handle adding a new comment
    const handleAddComment = () => {
        if (newComment.trim() !== '') {
            const currentDate = new Date().toLocaleString('en-US', { hour: 'numeric', minute: 'numeric', hour12: true });
            const commentData = {
                author: 'You', // Replace with actual user information if available
                timestamp: currentDate,
                text: newComment,
                replies: [],
            };
            setComments([...comments, commentData]);
            setNewComment('');
        }
    };

    // Function to handle adding a reply to a comment
    const handleAddReply = (index) => {
        if (replyText.trim() !== '') {
            const currentDate = new Date().toLocaleString('en-US', { hour: 'numeric', minute: 'numeric', hour12: true });
            const newReply = {
                author: 'You', // Replace with actual user information if available
                timestamp: currentDate,
                text: replyText,
            };
            const updatedComments = [...comments];
            updatedComments[index].replies.push(newReply);
            setComments(updatedComments);
            setReplyText('');
            setReplyingIndex(null);
        }
    };

    // Function to set the index of the comment being replied to
    const startReply = (index) => {
        setReplyingIndex(index);
    };

    return (
        <div style={{ paddingLeft: "100px", marginTop: "7rem" }}>
            <CommentGroup threaded size='large'>
                {comments.map((comment, index) => (
                    <Comment key={index}>
                        <CommentAvatar as='a' src='https://react.semantic-ui.com/images/avatar/small/matt.jpg' />
                        <CommentContent>
                            <CommentAuthor as='a'>{comment.author}</CommentAuthor>
                            <CommentMetadata>
                                <span>{comment.timestamp}</span>
                            </CommentMetadata>
                            <CommentText>{comment.text}</CommentText>
                            <CommentActions>
                                <a onClick={() => startReply(index)}>Reply</a>
                            </CommentActions>
                            {comment.replies.length > 0 && (
                                <CommentGroup >
                                    {comment.replies.map((reply, replyIndex) => (
                                        <Comment key={`${index}-${replyIndex}`}>
                                            <CommentAvatar as='a' src='https://react.semantic-ui.com/images/avatar/small/jenny.jpg' />
                                            <CommentContent>
                                                <CommentAuthor as='a'>{reply.author}</CommentAuthor>
                                                <CommentMetadata>
                                                    <span>{reply.timestamp}</span>
                                                </CommentMetadata>
                                                <CommentText>{reply.text}</CommentText>
                                            </CommentContent>
                                        </Comment>
                                    ))}
                                </CommentGroup>
                            )}
                            {replyingIndex === index && (
                                <Form reply>
                                    <FormTextArea
                                        value={replyText}
                                        onChange={(e) => setReplyText(e.target.value)}
                                        placeholder='Write a reply...'
                                    />
                                    <Button
                                        content='Add Reply'
                                        labelPosition='left'
                                        icon='edit'
                                        primary
                                        onClick={() => handleAddReply(index)}
                                    />
                                </Form>
                            )}
                        </CommentContent>
                    </Comment>
                ))}
                <Form reply>
                    <FormTextArea
                        value={newComment}
                        onChange={(e) => setNewComment(e.target.value)}
                        placeholder='Add a comment...'
                    />
                    <Button
                        content='Add Comment'
                        labelPosition='left'
                        icon='edit'
                        primary
                        onClick={handleAddComment}
                    />  
                    {/* Example upload button */}
                    <Button icon labelPosition='left' primary>
                        <Icon name='upload' />
                        Upload
                    </Button>
                </Form>
            </CommentGroup>
        </div>
    );
};

export default TeamFeed;
