import React, { useState, useEffect } from 'react';
import {
    CommentText,
    CommentMetadata,
    CommentGroup,
    CommentContent,
    CommentAvatar,
    CommentActions,
    CommentAuthor,
    Comment,
    Form,
    FormTextArea,
    Button,
    Icon,
} from 'semantic-ui-react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const TeamFeed = () => {
    const {teamId} = useParams();
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');
    const [replyText, setReplyText] = useState('');
    const [replyingIndex, setReplyingIndex] = useState(null);

    useEffect(() => {
        const fetchMessages = async () => {
            try {
                const response = await axios.get(`http://localhost:8000/feed/${teamId}`);
                setComments(response.data);
            } catch (error) {
                console.error('Error fetching messages:', error);
            }
        };

        fetchMessages();
    }, [teamId]);

    const handleAddComment = async () => {
        if (newComment.trim() !== '') {
            const currentDate = new Date().toLocaleString('en-US', { hour: 'numeric', minute: 'numeric', hour12: true });
            const commentData = {
                textContent: newComment,
                createdBy: {
                    firstName: 'You', // Replace with actual user's first name logic
                    lastName: '', // Replace with actual user's last name logic
                },
                createdAt: currentDate,
                replies: [],
            };

            try {
                const response = await axios.post('http://localhost:8000/feed/', {
                    textContent: newComment,
                    teamId: teamId,
                    userId: 'yourUserIdHere', // Replace with actual userId logic
                });

                const savedMessage = response.data;
                commentData._id = savedMessage._id; // Assuming saved message returns its ID
                setComments([...comments, commentData]);
                setNewComment('');
            } catch (error) {
                console.error('Error adding message:', error);
            }
        }
    };

    const handleAddReply = async (index) => {
        if (replyText.trim() !== '') {
            const currentDate = new Date().toLocaleString('en-US', { hour: 'numeric', minute: 'numeric', hour12: true });
            const newReply = {
                textContent: replyText,
                createdBy: {
                    firstName: 'You', // Replace with actual user's first name logic
                    lastName: '', // Replace with actual user's last name logic
                },
                createdAt: currentDate,
            };

            try {
                const response = await axios.post('http://localhost:8000/feed/', {
                    textContent: replyText,
                    teamId: teamId,
                    userId: 'yourUserIdHere', // Replace with actual userId logic
                });

                const savedMessage = response.data;
                newReply._id = savedMessage._id; // Assuming saved message returns its ID

                const updatedComments = [...comments];
                updatedComments[index].replies.push(newReply);
                setComments(updatedComments);
                setReplyText('');
                setReplyingIndex(null);
            } catch (error) {
                console.error('Error adding reply:', error);
            }
        }
    };

    const startReply = (index) => {
        setReplyingIndex(index);
    };

    return (
        <div style={{ paddingLeft: '100px', marginTop: '7rem', position: 'relative', overflow:"hidden" }}>
            <div style={{ paddingBottom: '4rem', marginBottom: '4rem' }}>
                <CommentGroup threaded size='large'>
                    {comments.map((comment, index) => ( // Define index here
                        <Comment key={comment._id}>
                            <CommentAvatar as='a' src='https://react.semantic-ui.com/images/avatar/small/matt.jpg' />
                            <CommentContent>
                                <CommentAuthor as='a'>{comment.createdBy.firstName} {comment.createdBy.lastName}</CommentAuthor>
                                <CommentMetadata>
                                    <span>{comment.createdAt}</span>
                                </CommentMetadata>
                                <CommentText>{comment.textContent}</CommentText>
                                <CommentActions>
                                    <a onClick={() => startReply(index)}>Reply</a>
                                </CommentActions>
