                                    <CommentGroup>
                                        {comment.replies.map((reply, replyIndex) => ( // Define replyIndex here
                                            <Comment key={reply._id}>
                                                <CommentAvatar as='a' src='https://react.semantic-ui.com/images/avatar/small/jenny.jpg' />
                                                <CommentContent>
                                                    <CommentAuthor as='a'>{reply.createdBy.firstName} {reply.createdBy.lastName}</CommentAuthor>
                                                    <CommentMetadata>
                                                        <span>{reply.createdAt}</span>
                                                    </CommentMetadata>
                                                    <CommentText>{reply.textContent}</CommentText>
                                                </CommentContent>
                                            </Comment>
                                        ))}
                                    </CommentGroup>
                                )}
                                {replyingIndex === index && (
                                    <Form reply>
                                        <FormTextArea
                                            value={replyText}
                                            onChange={(e) => setReplyText(e.target.value)}
                                            placeholder='Write a reply...'
                                        />
                                        <Button
                                            content='Add Reply'
                                            labelPosition='left'
                                            icon='edit'
                                            primary
                                            onClick={() => handleAddReply(index)}
                                        />
                                    </Form>
                                )} */}
                            </CommentContent>
                        </Comment>
                    ))}
                </CommentGroup>
            </div>
            <div style={{ position: 'fixed', bottom: '0', width: '50%', background: 'white', padding: '1rem' }}>
                <Form reply>
                    <FormTextArea
                        value={newComment}
                        onChange={(e) => setNewComment(e.target.value)}
                        placeholder='Send a message'
                    />
                    <Button
                        content='Send'
                        labelPosition='left'
                        icon='send'
                        onClick={handleAddComment}
                    />
                    <Button icon labelPosition='left' >
                        <Icon name='upload' />  
                        Upload File
                    </Button>
                </Form>
            </div>
        </div>
    );
};

export default TeamFeed;
