import React, { useContext } from 'react';
import { MessageContext } from '../api/ChatContext'; // Ensure this path is correct
import { useNavigate } from 'react-router-dom';

const AllChats = () => {
  const navigate = useNavigate();
  const { chats } = useContext(MessageContext);

  const handleChatClick = (user) => {
    navigate(`/chat/${user._id}`);
  };

  return (
    <div className="flex justify-center items-center h-screen bg-gray-100">
      <div className="max-w-lg w-full bg-white rounded-lg shadow-lg p-6">
        <h2 className="text-xl font-semibold mb-4">All Chats</h2>
        {chats.length > 0 ? (
          <div>
            {chats.map((chat, index) => (
              <div key={index} className="mb-4 p-4 cursor-pointer hover:bg-gray-200 rounded-lg flex items-center justify-between">
                <div className="flex items-center">
                  <img src={`https://avatars.dicebear.com/api/avataaars/${chat.user._id}${chat.lastName}.svg`} alt="Avatar" className="w-10 h-10 rounded-full mr-4" />
                  <div>
                    <p className="text-lg font-medium">{chat.firstName} {chat.lastName}</p>
                    {/* Add additional chat information if needed */}
                  </div>
                </div>
                <button className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 focus:outline-none" onClick={() => handleChatClick(chat)}>
                  Chat
                </button>
              </div>
            ))}
          </div>
        ) : (
          <p className="text-gray-600">No chats available.</p>
        )}
      </div>
    </div>
  );
};

export default AllChats;
