import React, { useState, useEffect } from 'react';
import { Button, Form, Header, List, Modal, Segment } from 'semantic-ui-react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';

const TeamTasks = () => {
  const location = useLocation();
  const teamId = location.state.teamId;
  const [taskDetail, setTaskDetail] = useState('');
  const [tasks, setTasks] = useState([]);
  const [modalOpen, setModalOpen] = useState(false);
  const [file, setFile] = useState(null);
  console.log(teamId);
  
  useEffect(() => {
    fetchTasks();
  }, [teamId]);

  const fetchTasks = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/files/${teamId}`);
      setTasks(response.data.uploadedContents);
      console.log(tasks) // Assuming response is an object with uploadedContents array
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const handleTaskSubmit = async () => {
    try {
      const formData = new FormData();
      formData.append('taskDetail', taskDetail);
      if (file) {
        formData.append('taskContent', file);
      }

      const response = await axios.post(`http://localhost:8000/files/${teamId}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      // Update tasks state with new task
      setTasks([...tasks, response.data]);

      // Clear form or perform other actions after successful upload
      setTaskDetail('');
      setFile(null);
      setModalOpen(false); // Close modal after submitting task
    } catch (error) {
      console.error('Error creating task:', error);
    }
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  return (
    <Segment>
      <Header as='h2'>Team Tasks</Header>
      
      {tasks.length > 0 ? (
        <List divided relaxed>
          {tasks.map(task => (
            <List.Item key={task._id}>
              <List.Icon name='file' />
              <List.Content>
                <List.Header>{task.taskDetail}</List.Header>
                <List.Description>Created by: {task.createdBy.firstName} {task.createdBy.lastName} </List.Description>
                <List.Description>At: {new Date(task.createdAt).toLocaleString()}</List.Description>
                <List.Description>Files: {task.taskContent}</List.Description>
              </List.Content>
            </List.Item>
          ))}
        </List>
      ) : (
        <p>No tasks available.</p>
      )}

      <Modal
        onClose={() => setModalOpen(false)}
        onOpen={() => setModalOpen(true)}
        open={modalOpen}
        trigger={<Button primary>Create Task</Button>}
      >
        <Modal.Header>Create New Task</Modal.Header>
        <Modal.Content>
          <Form>
            <Form.TextArea
              label='Task Detail'
              placeholder='Enter task details...'
              value={taskDetail}
              onChange={(e) => setTaskDetail(e.target.value)}
            />
            <Form.Input
              type='file'
              label='Task Content (Optional)'
              onChange={handleFileChange}
            />
          </Form>
        </Modal.Content>
        <Modal.Actions>
          <Button onClick={() => setModalOpen(false)}>Cancel</Button>
          <Button primary onClick={handleTaskSubmit}>Create Task</Button>
        </Modal.Actions>
      </Modal>
    </Segment>
  );
};

export default TeamTasks;
