import { Route, Routes } from 'react-router-dom';
import './App.css';
import Home from './Pages/Home';
import NavBar from './Components/NavBar';
import AuthPage from './Pages/AuthPage';
import UserProfile from './Pages/UserProfile';
import TeamPage from './Pages/TeamPage';
import UserDetails from './Pages/UserDetails';
import { useContext } from 'react';
import { authContext } from './api/AuthContext';

function App() {
  const { userState } = useContext(authContext);

  // Render Home if logged in, otherwise render AuthPage
  const renderPage = () => {
    if (userState === 'Logged-In') {
      return <Route path='/' element={<Home />} />;
    } else {
      return <Route path='/auth' element={<AuthPage />} />;
    }
  };

  return (
    <div>
      <NavBar /> {/* Always render NavBar */}
      <div className="flex-1 p-10 ml-56"> {/* Assuming ml-56 should always apply */}
        <Routes>
          {renderPage()}
          <Route path='/teamDetails/:teamId' element={<TeamPage />} />
          <Route path='/profile' element={<UserProfile />} />
          <Route path='/userProfile' element={<UserDetails />} />
        </Routes>
      </div>
    </div>
  );
}

export default App;
