import React, { useState, useEffect } from 'react';
import { Grid, Image, Card, CardContent, CardHeader, CardMeta, Segment, Icon, Button, Modal, Form } from 'semantic-ui-react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Teams = () => {
    const [teams, setTeams] = useState([]);
    const [open, setOpen] = useState(false);
    const [newTeamName, setNewTeamName] = useState('');
    const [members, setMembers] = useState([{ firstName: '', lastName: '', email: '' }]); // Initial member state
    const navigate = useNavigate();

    useEffect(() => {
        const fetchTeams = async () => {
            try {
                const response = await axios.get('http://localhost:8000/team');
                setTeams(response.data);
            } catch (error) {
                console.error('Error fetching teams:', error);
            }
        };

        fetchTeams();
    }, []);

    const navigateToTeamDetails = (teamId) => {
        navigate(`/${teamId}`);
    };

    const navigateToCreateTeam = () => {
        setOpen(true);
    };

    const handleCloseModal = () => {
        setOpen(false);
        // Reset form fields if needed
        setNewTeamName('');
        setMembers([{ firstName: '', lastName: '', email: '' }]);
    };

    const handleCreateTeam = async () => {
        try {
            const response = await axios.post('http://localhost:8000/team', {
                teamName: newTeamName,
                members: members,
            });
            console.log('Team created:', response.data);
            // Optionally, navigate to the newly created team's details page
            navigateToTeamDetails(response.data._id);
        } catch (error) {
            console.error('Error creating team:', error);
        }
        handleCloseModal();
    };

    const handleMemberChange = (index, event) => {
        const { name, value } = event.target;
        const newMembers = [...members];
        newMembers[index][name] = value;
        setMembers(newMembers);
    };

    const handleAddMember = () => {
        setMembers([...members, { firstName: '', lastName: '', email: '' }]);
    };

    return (
        <div>
            <Segment color='white' textAlign='center' style={{ padding: '1rem', marginBottom: '1rem' }}>
                <h1>Teams</h1>
                <Button primary onClick={navigateToCreateTeam}>Create Team</Button>
            </Segment>
            <div style={{ paddingLeft: "5rem", backgroundColor: "lightgrey", minHeight:"100vh", overflow:"hidden" }}>
                <Grid columns={5} container doubling stackable>
                    {teams.map(team => (
                        <Grid.Column key={team._id} style={{ marginBottom: '2rem', marginTop: "2rem" }}>
                            <div className="group overflow-hidden rounded-lg shadow-lg transition duration-300 ease-in-out transform hover:-translate-y-5 hover:shadow-xl cursor-pointer">
                                <Card onClick={() => navigateToTeamDetails(team._id)}>
                                    <Image src={team.members[0].avatar || 'https://react.semantic-ui.com/images/avatar/large/ade.jpg'} wrapped ui={false} />
                                    <CardContent>
                                        <CardHeader>{team.teamName}</CardHeader>
                                        <CardMeta>Joined in {new Date(team.createdAt).getFullYear()}</CardMeta>
                                    </CardContent>
                                    <CardContent extra>
                                        <a>
                                            <Icon name='users' />
                                            {team.memberCount} Members
                                        </a>
                                    </CardContent>
                                </Card>
                            </div>
                        </Grid.Column>
                    ))}
                </Grid>
            </div>

            <Modal
                onClose={handleCloseModal}
                open={open}
            >
                <Modal.Header>Create a New Team</Modal.Header>
                <Modal.Content>
                    <Form>
                        <Form.Field>
                            <label>Team Name</label>
                            <input
                                placeholder='Enter team name'
                                value={newTeamName}
                                onChange={(e) => setNewTeamName(e.target.value)}
                            />
                        </Form.Field>
                        {members.map((member, index) => (
                            <React.Fragment key={index}>
                                <Form.Group widths='equal'>
                                    <Form.Input
                                        fluid
                                        label='First Name'
                                        placeholder='First Name'
                                        name='firstName'
                                        value={member.firstName}
                                        onChange={(e) => handleMemberChange(index, e)}
                                    />
                                    <Form.Input
                                        fluid
                                        label='Last Name'
                                        placeholder='Last Name'
                                        name='lastName'
                                        value={member.lastName}
                                        onChange={(e) => handleMemberChange(index, e)}
                                    />
                                    <Form.Input
                                        fluid
                                        label='Email'
                                        placeholder='Email'
                                        name='email'
                                        value={member.email}
                                        onChange={(e) => handleMemberChange(index, e)}
                                    />
                                </Form.Group>
                            </React.Fragment>
                        ))}
                        <Button type='button' onClick={handleAddMember}>Add Member</Button>
                    </Form>
                </Modal.Content>
                <Modal.Actions>
                    <Button color='black' onClick={handleCloseModal}>Cancel</Button>
                    <Button
                        content='Create Team'
                        labelPosition='right'
                        icon='checkmark'
                        onClick={handleCreateTeam}
                        positive
                    />
                </Modal.Actions>
            </Modal>
        </div>
    );
};

export default Teams;
