import React, { useContext } from 'react';
import { MessageContext } from '../api/ChatContext'; // Ensure this path is correct
import { useNavigate } from 'react-router-dom';
import { List, Image } from 'semantic-ui-react';
import SearchBar from '../Components/SearchBar';

const AllChats = () => {
  const navigate = useNavigate();
  const { chats } = useContext(MessageContext);

  const handleChatClick = (user) => {
    navigate(`/chat/${user.userId}`);
  };

  const handleUserSelect = (selectedUser) => {
    const userId = selectedUser._id;
    navigate(`/chat/${userId}`);
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="px-4 py-3 border-b border-gray-200">
        <SearchBar onUserSelect={handleUserSelect} />
      </div>
      <div className="mt-6">
        {chats.length > 0 ? (
          <List divided relaxed>
            {chats.map((chat) => (
              <List.Item key={chat.id} onClick={() => handleChatClick(chat)} className="cursor-pointer">
                <Image avatar src={chat.profilePicUrl} />
                <List.Content>
                  <List.Header>{`${chat.firstName} ${chat.lastName}`}</List.Header>
                  <List.Description>{chat.lastMessage}</List.Description>
                  <List.Description className="text-gray-500">{chat.lastMessageTime}</List.Description>
                  {/* Add unread message count or indicator if needed */}
                </List.Content>
              </List.Item>
            ))}
          </List>
        ) : (
          <p className="text-center text-gray-500 mt-8">No chats available</p>
        )}
      </div>
    </div>
  );
};

export default AllChats;
