import React, { useContext, useEffect, useState } from 'react'
import { Button, Divider, Header, Image, List, ListContent, ListItem, Sticky, Accordion, Icon } from 'semantic-ui-react'
import { teamContext } from '../api/TeamsContext';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const TeamDetails = () => {
  const { teams, setTeams } = useContext(teamContext);
  const teamId = useParams();

  console.log(teamId);
  useEffect(() => {
    // const userId = localStorage.getItem('userId'); 
    const fetchTeams = async () => {
        try {
            const response = await axios.get(`http://localhost:8000/team/userTeam/${teamId}`);
            setTeams(response.data);
        } catch (error) {
            console.error('Error fetching teams:', error);
        }
    };

    fetchTeams();
}, [teamId]);

  const [activeIndex, setActiveIndex] = useState(-1);
  const handleClick = (e, titleProps) => {
    const { index } = titleProps;
    const newIndex = activeIndex === index ? -1 : index;
    setActiveIndex(newIndex);
  }

  

  return (
    <div>
      <Header as='h1' attached dividing textAlign='center' style={{ backgroundColor: "lightgrey" }}>
        {teams.teamName}
      </Header>
      <Accordion styled fluid>
        <Accordion.Title
          active={activeIndex === 0}
          index={0}
          onClick={handleClick}
        >
          <Icon name='dropdown' />
          Team Members
        </Accordion.Title>
        <Accordion.Content active={activeIndex === 0}>
          <List divided verticalAlign='middle'>
            <ListItem>
              <ListContent floated='right'>
                <Button>Message</Button>
              </ListContent>
              <Image avatar src='https://react.semantic-ui.com/images/avatar/small/lena.png' />
              <ListContent>Lena</ListContent>
            </ListItem>
            <ListItem>
              <ListContent floated='right'>
                <Button>Message</Button>
              </ListContent>
              <Image avatar src='https://react.semantic-ui.com/images/avatar/small/lindsay.png' />
              <ListContent>Lindsay</ListContent>
            </ListItem>
            <ListItem>
              <ListContent floated='right'>
                <Button>Message</Button>
              </ListContent>
              <Image avatar src='https://react.semantic-ui.com/images/avatar/small/mark.png' />
              <ListContent>Mark</ListContent>
            </ListItem>
            <ListItem>
              <ListContent floated='right'>
                <Button>Message</Button>
              </ListContent>
              <Image avatar src='https://react.semantic-ui.com/images/avatar/small/molly.png' />
              <ListContent>Molly</ListContent>
            </ListItem>
          </List>
        </Accordion.Content>
      </Accordion>
      <Divider horizontal> Feed </Divider>
    </div>
  )
}

export default TeamDetails
