import React, { useContext } from 'react';
import { MessageContext } from '../api/ChatContext'; // Make sure this path is correct
import { useNavigate } from 'react-router-dom';
import SearchBar from '../Components/SearchBar';

const AllChats = () => {
  const navigate = useNavigate();
  const { chats } = useContext(MessageContext);

  const handleChatClick = (user) => {
    navigate(`/chat/${user.userId}`);
  };

  const handleUserSelect = (selectedUser) => {
    const userId = selectedUser._id;
    navigate(`/chat/${userId}`);
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="px-4 py-3 border-b border-gray-200">
        <SearchBar onUserSelect={handleUserSelect} />
      </div>
      <div className="mt-6">
        {chats.length > 0 ? (
          <ul className="divide-y divide-gray-200">
            {chats.map((chat) => (
              <li
                key={chat.id}
                onClick={() => handleChatClick(chat)}
                className="flex items-center justify-between px-4 py-4 cursor-pointer hover:bg-gray-100"
              >
                <div className="flex items-center">
                  <img
                    className="h-12 w-12 rounded-full object-cover"
                    src={chat.profilePicUrl}
                    alt={`${chat.firstName} ${chat.lastName}`}
                  />
                  <div className="ml-4">
                    <p className="text-lg font-medium text-gray-900 truncate">
                      {chat.firstName} {chat.lastName}
                    </p>
                    <p className="text-sm text-gray-500 truncate">{chat.lastMessage}</p>
                  </div>
                </div>
                <div className="flex flex-col items-end">
                  <p className="text-sm text-gray-500">{chat.lastMessageTime}</p>
                  {/* Add unread message count or indicator if needed */}
                </div>
              </li>
            ))}
          </ul>
        ) : (
          <p className="text-center text-gray-500 mt-8">No chats available</p>
        )}
      </div>
    </div>
  );
};

export default AllChats;
