import React, { useState, useEffect } from 'react';
import { Button, Form, Header, List, Segment } from 'semantic-ui-react';
import axios from 'axios';

const TeamTasks = ({ teamId }) => {
  const [taskDetail, setTaskDetail] = useState('');
  const [userId, setUserId] = useState(''); // Assuming you have user ID stored or fetched
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    fetchTasks();
  }, [teamId]);

  const fetchTasks = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/team/tasks/${teamId}`);
      setTasks(response.data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const handleTaskSubmit = async () => {
    try {
      const formData = new FormData();
      formData.append('taskContent', taskDetail);
      formData.append('userId', userId);

      const response = await axios.post(`http://localhost:8000/team/tasks/${teamId}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      // Update tasks state with new task
      setTasks([...tasks, response.data]);

      // Clear form or perform other actions after successful upload
      setTaskDetail('');
    } catch (error) {
      console.error('Error creating task:', error);
    }
  };

  return (
    <Segment>
      <Header as='h2'>Team Tasks</Header>
      
      {tasks.length > 0 ? (
        <List divided relaxed>
          {tasks.map(task => (
            <List.Item key={task._id}>
              <List.Icon name='file' />
              <List.Content>
                <List.Header>{task.taskDetail}</List.Header>
                <List.Description>Created by: {task.createdBy}</List.Description>
              </List.Content>
            </List.Item>
          ))}
        </List>
      ) : (
        <p>No tasks available.</p>
      )}

      <Form>
        <Form.TextArea
          label='Task Detail'
          placeholder='Enter task details...'
          value={taskDetail}
          onChange={(e) => setTaskDetail(e.target.value)}
        />
        <Button primary onClick={handleTaskSubmit}>Create Task</Button>
      </Form>
    </Segment>
  );
};

export default TeamTasks;
