import React, { useEffect, useRef, useState } from 'react';
import { Button, Sidebar, Menu, Icon } from 'semantic-ui-react';
import TeamDetails from '../Components/TeamDetails';
import TeamFeed from '../Components/TeamFeed';

const TeamPage = () => {
  const bottomRef = useRef(null);
  const [visible, setVisible] = useState(false);

  const toggleSidebar = () => {
    setVisible(!visible);
  };

  useEffect(() => {
    const scrollToBottom = () => {
      bottomRef.current.scrollIntoView({ behavior: 'smooth' });
      console.log("scroll");
    };

    setTimeout(scrollToBottom, 100);
  }, []);

  return (
    <div className="relative">
      <Sidebar.Pushable>
        <Sidebar
          as={Menu}
          animation='overlay'
          icon='labeled'
          inverted
          vertical
          visible={visible}
          direction='right'
          width='thin'
        >
          <Menu.Item as='a'>
            <Icon name='home' />
            Home
          </Menu.Item>
          <Menu.Item as='a'>
            <Icon name='user' />
            Profile
          </Menu.Item>
          <Menu.Item as='a'>
            <Icon name='settings' />
            Settings
          </Menu.Item>
        </Sidebar>
        <Sidebar.Pusher>
          <div className="p-4">
            <Button onClick={toggleSidebar} icon>
              <Icon name='sidebar' />
            </Button>
            <TeamDetails />
            <div className="mt-4">
              <TeamFeed />
            </div>
            <div ref={bottomRef} />
          </div>
        </Sidebar.Pusher>
      </Sidebar.Pushable>
    </div>
  );
};

export default TeamPage;
