import React, { useState, useEffect } from 'react';
import {
    CommentText,
    CommentMetadata,
    CommentGroup,
    CommentContent,
    CommentAvatar,
    CommentActions,
    CommentAuthor,
    Comment,
    Form,
    FormTextArea,
    Button,
    Icon,
    Divider,
} from 'semantic-ui-react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import Cookies from 'js-cookie';
import useDownloader from 'react-use-downloader';

const TeamFeed = () => {
    const { teamId } = useParams();
    const navigate = useNavigate();
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');
    const [newFile, setNewFile] = useState(null);
    const [filePreview, setFilePreview] = useState(null);
    const [replyText, setReplyText] = useState('');
    const [replyingIndex, setReplyingIndex] = useState(null);
    const { download } = useDownloader();

    useEffect(() => {
        fetchMessages(); // Initial fetch

        const interval = setInterval(() => {
            fetchMessages(); // Fetch messages every 10 seconds (adjust as needed)
        }, 10000);

        return () => {
            clearInterval(interval); // Clean up interval on component unmount
        };
    }, [teamId]);

    const fetchMessages = async () => {
        try {
            const response = await axios.get(`http://localhost:8000/feed/${teamId}`);
            setComments(response.data);
        } catch (error) {
            console.error('Error fetching messages:', error);
        }
    };

    const getCurrentUser = async () => {
        try {
            const userId = Cookies.get('userId');
            if (!userId) {
                console.error('User ID not found in cookies.');
                return null;
            }
            const response = await axios.get(`http://localhost:8000/userAuth/${userId}`);
            return response.data;
        } catch (error) {
            console.error('Error fetching user:', error);
            return null;
        }
    };

    const handleAddComment = async () => {
        if (newComment.trim() !== '' || newFile) {
            const userId = Cookies.get('userId');

            const currentUser = await getCurrentUser();
            if (!currentUser) {
                console.error('User not found.');
                return;
            }

            const formData = new FormData();
            formData.append('textContent', newComment);
            formData.append('teamId', teamId);
            formData.append('userId', userId);
            if (newFile) {
                formData.append('uploadContent', newFile);
            }

            try {
                const response = await axios.post('http://localhost:8000/feed/', formData, {
                    headers: {
                        'Content-Type': 'multipart/form-data',
                    },
                });

                const savedMessage = response.data;
                const commentData = {
                    _id: savedMessage._id,
                    textContent: newComment,
                    uploadContent: savedMessage.uploadContent,
                    createdBy: {
                        _id: userId,
                        firstName: currentUser.firstName,
                        lastName: currentUser.lastName,
                    },
                    createdAt: new Date(savedMessage.createdAt).toLocaleString('en-US', { hour: 'numeric', minute: 'numeric', hour12: true }),
                    replies: [],
                };
                setComments([...comments, commentData]);
                setNewComment('');
                setNewFile(null);
                setFilePreview(null);
            } catch (error) {
                console.error('Error adding message:', error);
            }
        }
    };

    const handleAddReply = async (commentId) => {
        if (replyText.trim() !== '') {
            const userId = Cookies.get('userId');
            const currentUser = await getCurrentUser();
            if (!currentUser) {
                console.error('User not found.');
                return;
            }

            const replyData = {
                textContent: replyText,
                createdBy: {
                    _id: userId,
                    firstName: currentUser.firstName,
                    lastName: currentUser.lastName,
                },
                createdAt: new Date().toLocaleString('en-US', { hour: 'numeric', minute: 'numeric', hour12: true }),
            };

            try {
                const response = await axios.post(`http://localhost:8000/feed/${commentId}/reply`, {
                    textContent: replyText,
                    userId: userId,
                });

                const updatedComments = comments.map(comment =>
                    comment._id === commentId
                        ? {
                            ...comment,
                            replies: [...comment.replies, { ...replyData, _id: response.data._id }],
                        }
                        : comment
                );

                setComments(updatedComments);
                setReplyText('');
                setReplyingIndex(null);
            } catch (error) {
                console.error('Error adding reply:', error);
            }
        }
    };

    const startReplying = (index) => {
        setReplyingIndex(index);
    };

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        setNewFile(file);
        setFilePreview(URL.createObjectURL(file));
    };

    return (
        <div className="team-feed">
            <CommentGroup>
                <h3>Team Feed</h3>
                <Divider />
                {comments.map((comment, index) => (
                    <Comment key={comment._id}>
                        <CommentAvatar src={comment.createdBy.profileImageURL} />
                        <CommentContent>
                            <CommentAuthor as="a">{`${comment.createdBy.firstName} ${comment.createdBy.lastName}`}</CommentAuthor>
                            <CommentMetadata>
                                <div>{new Date(comment.createdAt).toLocaleString('en-US', { hour: 'numeric', minute: 'numeric', hour12: true })}</div>
                            </CommentMetadata>
                            <CommentText>{comment.textContent}</CommentText>
                            {comment.uploadContent && (
                                <div>
                                    <a href={`http://localhost:8000${comment.uploadContent}`} target="_blank" rel="noopener noreferrer">
                                        <Icon name='file' /> View Attachment
                                    </a>
                                </div>
                            )}
                            <CommentActions>
                                <a onClick={() => startReplying(index)}>Reply</a>
                            </CommentActions>
                            {replyingIndex === index && (
                                <Form reply>
                                    <FormTextArea
                                        value={replyText}
                                        onChange={(e) => setReplyText(e.target.value)}
                                        placeholder="Type your reply..."
                                    />
                                    <Button
                                        content="Add Reply"
                                        labelPosition="left"
                                        icon="edit"
                                        primary
                                        onClick={() => handleAddReply(comment._id)}
                                    />
                                </Form>
                            )}
                            {comment.replies.length > 0 && (
                                <CommentGroup>
                                    {comment.replies.map((reply) => (
                                        <Comment key={reply._id}>
                                            <CommentAvatar src={reply.createdBy.profileImageURL} />
                                            <CommentContent>
                                                <CommentAuthor as="a">{`${reply.createdBy.firstName} ${reply.createdBy.lastName}`}</CommentAuthor>
                                                <CommentMetadata>
                                                    <div>{new Date(reply.createdAt).toLocaleString('en-US', { hour: 'numeric', minute: 'numeric', hour12: true })}</div>
                                                </CommentMetadata>
                                                <CommentText>{reply.textContent}</CommentText>
                                            </CommentContent>
                                        </Comment>
                                    ))}
                                </CommentGroup>
                            )}
                        </CommentContent>
                    </Comment>
                ))}
                <Form reply>
                    <FormTextArea
                        value={newComment}
                        onChange={(e) => setNewComment(e.target.value)}
                        placeholder="Type a new comment..."
                    />
                    <input type="file" onChange={handleFileChange} />
                    {filePreview && (
                        <div>
                            <img src={filePreview} alt="Preview" style={{ maxWidth: '200px', maxHeight: '200px' }} />
                        </div>
                    )}
                    <Button content="Add Comment" labelPosition="left" icon="edit" primary onClick={handleAddComment} />
                </Form>
            </CommentGroup>
        </div>
    );
};

export default TeamFeed;
