import React, { useState, useEffect } from 'react';
import { Icon, Menu, Grid, GridColumn, Search, Segment, Header } from 'semantic-ui-react';
import axios from 'axios';
import SearchBar from './SearchBar';
import { useNavigate } from 'react-router-dom';

const NavBar = () => {
    const navigate = useNavigate();
    const handleUserSelect = (user) => {
        navigate('/profile', { state: { user } }); 
    };

    return (
        <div className="flex flex-col">
            {/* TOPBAR */}
            <div className="flex items-center justify-between p-4 bg-white text-white h-16 fixed w-full z-10">
                <div className="flex items-center justify-start w-1/3">
                    <img src="https://react.semantic-ui.com/logo.png" alt="App Logo" className="h-10 w-10" />
                </div>
                <div className="flex items-center justify-center w-1/3">
                    <SearchBar onUserSelect={handleUserSelect} />
                </div>
                <div className="flex items-center justify-end w-1/3">
                    <Icon name="sun" size="big" className="mr-4 cursor-pointer" color="black" />
                    <Icon name="user circle" size="big" className="cursor-pointer" color="black" onClick={(user)=>{navigate('/profile',{ state: { user } })}}/>
                </div>
            </div>
            {/* CONTENT */}
            <div className="flex mt-20">
                {/* SIDEBAR */}
                <Menu vertical fixed="left" className="h-full w-56 bg-white" style={{ marginTop: '7.6rem' }}>
                    <Menu.Item onClick={() => { navigate('/') }}>
                        <Icon name="sitemap" />
                        Teams
                    </Menu.Item>
                    <Menu.Item>
                        <Icon name="tasks" />
                        Tasks
                    </Menu.Item>
                    <Menu.Item>
                        <Icon name="chat" />
                        Chat
                    </Menu.Item>
                    <Menu.Item>
                        <Icon name="calendar" />
                        Calendar
                    </Menu.Item>
                    <Menu.Item>
                        <Icon name="settings" />
                        Settings
                    </Menu.Item>
                </Menu>
            </div>
        </div>
    );
};

export default NavBar;
